function casePc() {

    var block = document.getElementById('myCaseBlock');
    var btn = document.getElementById('caseBtn');

    btn.onclick = function () {
        block.style.display = "block";
    }

    window.onclick = function (event) {
        if (event.target == block) {
            block.style.display = "none";
        }
    }
}


function mobo() {

    var block_mobo = document.getElementById('myMoboBlock');
    var Mobobtn = document.getElementById('moboBtn');

    Mobobtn.onclick = function () {
        block_mobo.style.display = "block";
    }

    window.onclick = function (event) {
        if (event.target == block_mobo) {
            block_mobo.style.display = "none";
        }
    }
}

